import{_ as e,c as t,ag as r,o}from"./chunks/framework.BQhgNBqr.js";const h=JSON.parse('{"title":"旅遊須知 -> 護照 -> 首次申請","description":"","frontmatter":{},"headers":[],"relativePath":"旅遊須知/護照/首次申請.md","filePath":"docs/_旅遊須知/護照/首次申請.md","lastUpdated":1677764959000}'),l={name:"旅遊須知/護照/首次申請.md"};function i(n,a,_,s,d,p){return o(),t("div",null,a[0]||(a[0]=[r('<h1 id="首次申請" tabindex="-1">首次申請 <a class="header-anchor" href="#首次申請" aria-label="Permalink to &quot;首次申請&quot;">​</a></h1><h2 id="辦理方式" tabindex="-1">辦理方式 <a class="header-anchor" href="#辦理方式" aria-label="Permalink to &quot;辦理方式&quot;">​</a></h2><p>必須親自辦理，親辦方式如下：</p><ol><li><p>本人親自至外交部領事事務局（以下簡稱領務局）或外交部中、南、東部或雲嘉南辦事處（以下簡稱外交部四辦）辦理。</p></li><li><p>若本人未能以上述方式親自辦理，亦可親自持憑申請護照應備文件，向全國任一戶政事務所（以下簡稱戶所）臨櫃辦理「人別確認」後，再委任代理人（如親屬、旅行社等）續辦護照。（請另詳「首次申請護照人別確認說明書」【戶政事務所辦理人別確認後，申請人續委任代理人代辦護照專用】）</p></li><li><p>若無急需使用護照，亦可在戶所申辦護照。（請另詳「戶政事務所受理「首次申請護照親辦一處收件全程服務」說明書【申請人在戶所申辦護照之「一站式服務」專用】」）</p></li></ol><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to &quot;相關連結&quot;">​</a></h2><p><a href="https://www.boca.gov.tw/cp-18-7140-fc0d5-1.html" target="_blank" rel="noreferrer">在臺有戶籍國民在國內首次申請普通護照說明書</a></p>',6)]))}const f=e(l,[["render",i]]);export{h as __pageData,f as default};
