import{_ as a,c as r,ag as i,o}from"./chunks/framework.BQhgNBqr.js";const c=JSON.parse('{"title":"日本 -> 北海道 -> 活動 -> 北海道滑雪資訊","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/活動/滑雪.md","filePath":"docs/日本/北海道/活動/滑雪.md","lastUpdated":1737303891000}'),t={name:"日本/北海道/活動/滑雪.md"};function l(s,e,d,n,m,h){return o(),r("div",null,e[0]||(e[0]=[i('<h1 id="北海道滑雪資訊" tabindex="-1">北海道滑雪資訊 <a class="header-anchor" href="#北海道滑雪資訊" aria-label="Permalink to &quot;北海道滑雪資訊&quot;">​</a></h1><h2 id="適合滑雪的月份" tabindex="-1">適合滑雪的月份 <a class="header-anchor" href="#適合滑雪的月份" aria-label="Permalink to &quot;適合滑雪的月份&quot;">​</a></h2><p>滑雪最佳時機，日本的滑雪季一般由十二月開始並持續到四月。但實際的日期會根據各滑雪場與度假村公告為準。因一、二月的降雪量較穩定，這兩個月也是滑雪的高鋒期。</p><h2 id="滑雪場" tabindex="-1">滑雪場 <a class="header-anchor" href="#滑雪場" aria-label="Permalink to &quot;滑雪場&quot;">​</a></h2><h3 id="二世谷-moiwa-滑雪場" tabindex="-1">二世谷 Moiwa 滑雪場 <a class="header-anchor" href="#二世谷-moiwa-滑雪場" aria-label="Permalink to &quot;二世谷 Moiwa 滑雪場&quot;">​</a></h3><p>日本北海道二世古又稱新雪谷 (Hokkaido-Niseko)，擁有全日本也是全世界最著名的粉雪，雪道變化及場地廣闊，更是滑雪客首選，此區有最多的外國觀光客，年年雪票住宿交通都是直線上漲，對於中高階或是專家及以上的玩家來說追求雪況與粉雪是值得付出，初學者去可能相對 CP 值已經跟不上漲價速度。</p><ul><li>地址 〒048-1511 北海道虻田郡ニセコ町ニセコ 448</li><li>電話號碼 +81136592511</li><li><a href="https://niseko-moiwa.jp/" target="_blank" rel="noreferrer">官方網站</a></li></ul><iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2925.167029340544!2d140.62774724002065!3d42.848206204243276!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x5f0aa57af3e3dd49%3A0x16c5c9619866ce1a!2sNiseko%20Moiwa%20Ski%20Resort!5e0!3m2!1sen!2stw!4v1690558227479!5m2!1sen!2stw" width="600" height="450" style="border:0;" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe><h3 id="富良野滑雪場" tabindex="-1">富良野滑雪場 <a class="header-anchor" href="#富良野滑雪場" aria-label="Permalink to &quot;富良野滑雪場&quot;">​</a></h3><p>富良野滑雪場分成兩區，有可享受長程路線的「富良野區」，以及滑道種類豐富的「北之峰區」。場內設有 8 座吊椅，6 人座的纜車以及可容納 101 人的大型空中纜車等設備。搭乘纜車僅需 5 分鐘即可到達最高的山頂站。</p><ul><li>地址 〒076-8511 北海道富良野市中御料</li><li>電話號碼 +81167221111</li><li><a href="http://www.princehotels.co.jp/ski/furano/" target="_blank" rel="noreferrer">官方網站</a></li></ul><iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1714.1525083780543!2d142.35249035603783!3d43.32468310078149!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x5f73525ef98cb63d%3A0x9020eebf4f63234b!2sFurano%20ski%20resort!5e0!3m2!1sen!2stw!4v1690558382095!5m2!1sen!2stw" width="600" height="450" style="border:0;" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe><h3 id="札幌手稻滑雪場" tabindex="-1">札幌手稻滑雪場 <a class="header-anchor" href="#札幌手稻滑雪場" aria-label="Permalink to &quot;札幌手稻滑雪場&quot;">​</a></h3><p>位於劄幌市內，交通方便，海拔 1000m 高度的優質粉雪！豐富多彩的 15 條滑道吸引每個滑雪愛好者。</p><ul><li>地址 〒006-0029 北海道札幌市手稲区手稲本町 593-3</li><li>電話號碼 +81116826000</li><li><a href="https://sapporo-teine.com/snow/" target="_blank" rel="noreferrer">官方網站</a></li></ul><iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2914.3062677479115!2d141.19752624003317!3d43.077056089624286!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x5f0ad877c4c85785%3A0x9133d5952998f467!2sSapporoteine%20Ski%20Area!5e0!3m2!1sen!2stw!4v1690558445115!5m2!1sen!2stw" width="600" height="450" style="border:0;" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe>',16)]))}const p=a(t,[["render",l]]);export{c as __pageData,p as default};
