import{_ as r,c as l,ag as t,o as a}from"./chunks/framework.BQhgNBqr.js";const c=JSON.parse('{"title":"日本 -> 東京 -> 交通 -> 東京機場交通指南","description":"","frontmatter":{},"headers":[],"relativePath":"日本/東京/交通/機場.md","filePath":"docs/日本/東京/_交通/機場.md","lastUpdated":1732513528000}'),o={name:"日本/東京/交通/機場.md"};function e(n,i,s,u,g,h){return a(),l("div",null,i[0]||(i[0]=[t('<h1 id="東京機場交通指南" tabindex="-1">東京機場交通指南 <a class="header-anchor" href="#東京機場交通指南" aria-label="Permalink to &quot;東京機場交通指南&quot;">​</a></h1><p>東京有三個主要機場：成田國際機場（NRT）、羽田國際機場（HND）和茨城機場（IBR）。以下是從這些機場到東京市區的交通選擇。</p><h2 id="成田國際機場-nrt-→-東京市區" tabindex="-1">成田國際機場（NRT）→ 東京市區 <a class="header-anchor" href="#成田國際機場-nrt-→-東京市區" aria-label="Permalink to &quot;成田國際機場（NRT）→ 東京市區&quot;">​</a></h2><h3 id="成田特快-narita-express-n-ex" tabindex="-1">成田特快（Narita Express, N&#39;EX） <a class="header-anchor" href="#成田特快-narita-express-n-ex" aria-label="Permalink to &quot;成田特快（Narita Express, N&#39;EX）&quot;">​</a></h3><ul><li><strong>時間</strong>：60-90 分鐘</li><li><strong>價格</strong>：約 3000 日圓</li><li><strong>優點</strong>： <ul><li>直達東京站、新宿、池袋等主要車站</li><li>舒適度高</li><li>有專門的行李存放區</li></ul></li><li><strong>購票</strong>：可在機場的 JR 售票處或自動售票機購買</li><li><strong>班次</strong>：每 30-60 分鐘一班</li></ul><h3 id="京成-skyliner" tabindex="-1">京成 Skyliner <a class="header-anchor" href="#京成-skyliner" aria-label="Permalink to &quot;京成 Skyliner&quot;">​</a></h3><ul><li><strong>時間</strong>：約 41 分鐘（到日暮里）</li><li><strong>價格</strong>：約 2500 日圓</li><li><strong>優點</strong>： <ul><li>速度最快</li><li>連接日暮里和上野</li><li>舒適的座位和寬敞的空間</li></ul></li><li><strong>購票</strong>：可在機場的京成售票處或線上預訂</li><li><strong>班次</strong>：每 20-40 分鐘一班</li></ul><h3 id="京成本線普通列車" tabindex="-1">京成本線普通列車 <a class="header-anchor" href="#京成本線普通列車" aria-label="Permalink to &quot;京成本線普通列車&quot;">​</a></h3><ul><li><strong>時間</strong>：70-90 分鐘</li><li><strong>價格</strong>：約 1000 日圓</li><li><strong>優點</strong>： <ul><li>經濟實惠</li><li>可使用西瓜卡（Suica）或 PASMO 卡</li></ul></li><li><strong>缺點</strong>：較慢，可能需要站立</li><li><strong>班次</strong>：每 15-20 分鐘一班</li></ul><h3 id="機場巴士-airport-limousine-bus" tabindex="-1">機場巴士（Airport Limousine Bus） <a class="header-anchor" href="#機場巴士-airport-limousine-bus" aria-label="Permalink to &quot;機場巴士（Airport Limousine Bus）&quot;">​</a></h3><ul><li><strong>時間</strong>：60-120 分鐘（視交通狀況而定）</li><li><strong>價格</strong>：1000-3000 日圓</li><li><strong>優點</strong>： <ul><li>直達市內主要酒店或景點</li><li>適合不想搬運行李的旅客</li><li>舒適的座位</li></ul></li><li><strong>購票</strong>：可在機場的巴士售票處或線上預訂</li><li><strong>班次</strong>：視目的地而定，通常每 30-60 分鐘一班</li></ul><h2 id="羽田國際機場-hnd-→-東京市區" tabindex="-1">羽田國際機場（HND）→ 東京市區 <a class="header-anchor" href="#羽田國際機場-hnd-→-東京市區" aria-label="Permalink to &quot;羽田國際機場（HND）→ 東京市區&quot;">​</a></h2><h3 id="東京單軌電車-tokyo-monorail" tabindex="-1">東京單軌電車（Tokyo Monorail） <a class="header-anchor" href="#東京單軌電車-tokyo-monorail" aria-label="Permalink to &quot;東京單軌電車（Tokyo Monorail）&quot;">​</a></h3><ul><li><strong>時間</strong>：15-20 分鐘（到濱松町）</li><li><strong>價格</strong>：約 500 日圓</li><li><strong>優點</strong>： <ul><li>連接山手線，快速抵達市中心</li><li>景色優美，可欣賞東京灣</li></ul></li><li><strong>班次</strong>：每 4-10 分鐘一班</li></ul><h3 id="京急電鐵-keikyu-line" tabindex="-1">京急電鐵（Keikyu Line） <a class="header-anchor" href="#京急電鐵-keikyu-line" aria-label="Permalink to &quot;京急電鐵（Keikyu Line）&quot;">​</a></h3><ul><li><strong>時間</strong>：15-30 分鐘（到品川）</li><li><strong>價格</strong>：300-500 日圓</li><li><strong>優點</strong>： <ul><li>價格便宜</li><li>轉乘方便，可直接連接地鐵</li></ul></li><li><strong>班次</strong>：每 5-10 分鐘一班</li></ul><h3 id="機場巴士-airport-limousine-bus-1" tabindex="-1">機場巴士（Airport Limousine Bus） <a class="header-anchor" href="#機場巴士-airport-limousine-bus-1" aria-label="Permalink to &quot;機場巴士（Airport Limousine Bus）&quot;">​</a></h3><ul><li><strong>時間</strong>：30-60 分鐘</li><li><strong>價格</strong>：1000-1500 日圓</li><li><strong>優點</strong>： <ul><li>直達市區酒店和景點</li><li>舒適，適合攜帶行李的旅客</li></ul></li><li><strong>班次</strong>：視目的地而定，通常每 15-30 分鐘一班</li></ul><h2 id="茨城機場-ibr-→-東京市區" tabindex="-1">茨城機場（IBR）→ 東京市區 <a class="header-anchor" href="#茨城機場-ibr-→-東京市區" aria-label="Permalink to &quot;茨城機場（IBR）→ 東京市區&quot;">​</a></h2><h3 id="機場巴士-airport-limousine-bus-2" tabindex="-1">機場巴士（Airport Limousine Bus） <a class="header-anchor" href="#機場巴士-airport-limousine-bus-2" aria-label="Permalink to &quot;機場巴士（Airport Limousine Bus）&quot;">​</a></h3><ul><li><strong>時間</strong>：100-120 分鐘</li><li><strong>價格</strong>：500-1200 日圓</li><li><strong>優點</strong>： <ul><li>無需轉乘，直達東京站或秋葉原站</li><li>價格實惠</li></ul></li><li><strong>班次</strong>：配合航班時間，通常每 1-2 小時一班</li></ul><h3 id="列車-轉乘方式" tabindex="-1">列車（轉乘方式） <a class="header-anchor" href="#列車-轉乘方式" aria-label="Permalink to &quot;列車（轉乘方式）&quot;">​</a></h3><ul><li><strong>路線</strong>：茨城機場 → 連絡巴士到石岡站 → JR 常磐線 → 東京</li><li><strong>時間</strong>：120-150 分鐘</li><li><strong>價格</strong>：1500-2000 日圓</li><li><strong>優點</strong>： <ul><li>利用常磐線列車，適合想靈活轉乘的旅客</li><li>適合要在關東東北方郊區或從日本東北下來不經過東京市區的人。</li><li>可欣賞沿途風景</li></ul></li><li><strong>注意</strong>：需要多次轉乘，不適合攜帶大量行李的旅客</li></ul><h2 id="相關資訊來源" tabindex="-1">相關資訊來源 <a class="header-anchor" href="#相關資訊來源" aria-label="Permalink to &quot;相關資訊來源&quot;">​</a></h2><ul><li><a href="https://www.narita-airport.jp/ch2/" target="_blank" rel="noreferrer">成田國際機場官方網站</a></li><li><a href="https://tokyo-haneda.com/zh-CHT/index.html" target="_blank" rel="noreferrer">羽田機場官方網站</a></li><li><a href="https://www.ibaraki-airport.net/en/" target="_blank" rel="noreferrer">茨城機場官方網站</a></li><li><a href="https://www.jreast.co.jp/tc/nex/" target="_blank" rel="noreferrer">JR 東日本 - 成田特快</a></li><li><a href="https://www.keisei.co.jp/keisei/tetudou/skyliner/tc/index.php" target="_blank" rel="noreferrer">京成電鐵 - Skyliner</a></li><li><a href="https://www.tokyometro.jp/tcn/" target="_blank" rel="noreferrer">東京 Metro 地鐵官方網站</a></li><li><a href="https://www.limousinebus.co.jp/en/" target="_blank" rel="noreferrer">Airport Limousine Bus 官方網站</a></li><li><a href="https://www.gotokyo.org/tc/plan/getting-around/index.html" target="_blank" rel="noreferrer">東京觀光官方網站 - 交通資訊</a></li></ul>',25)]))}const p=r(o,[["render",e]]);export{c as __pageData,p as default};
