import{_ as a,c as t,ag as r,o as s}from"./chunks/framework.BQhgNBqr.js";const h=JSON.parse('{"title":"瑞士 -> 琉森州 -> 琉森湖 (Vierwaldstättersee)","description":"","frontmatter":{},"headers":[],"relativePath":"瑞士/琉森州/琉森湖.md","filePath":"docs/瑞士/琉森州/琉森湖.md","lastUpdated":1737303891000}'),i={name:"瑞士/琉森州/琉森湖.md"};function o(d,e,l,n,c,m){return s(),t("div",null,e[0]||(e[0]=[r('<h1 id="琉森湖-vierwaldstattersee" tabindex="-1">琉森湖 (Vierwaldstättersee) <a class="header-anchor" href="#琉森湖-vierwaldstattersee" aria-label="Permalink to &quot;琉森湖 (Vierwaldstättersee)&quot;">​</a></h1><p>琉森湖 (德語：Vierwaldstättersee, 英語:Lake Lucerne) 位於瑞士中部，處於四森林州之間。四森林州包括琉森州、烏里州、舒維茲州及以前的翁特瓦爾登州 (即現在的上瓦爾登半州及下瓦爾登半州)。</p><p>因為湖岸邊有不少的高山，如皮拉圖斯峰、施坦斯峰、里吉峰等，湖光山色構成典型的瑞士風景，美不勝收。不論瑞士人或外國人，都被琉森湖壯麗的風景吸引到來，使之成為旅遊勝地，湖岸上有不少旅店及渡假設施。</p><h2 id="地圖" tabindex="-1">地圖 <a class="header-anchor" href="#地圖" aria-label="Permalink to &quot;地圖&quot;">​</a></h2><iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d174194.34982296548!2d8.287086229262032!3d46.98552369691861!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x47855823936105cf%3A0xa60b34b5c10b1570!2sLake%20Lucerne!5e0!3m2!1sen!2stw!4v1690743493907!5m2!1sen!2stw" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to &quot;相關連結&quot;">​</a></h2><ul><li><a href="https://zh.wikipedia.org/zh-tw/%E7%90%89%E6%A3%AE%E6%B9%96" target="_blank" rel="noreferrer">Wikipedia - 琉森湖</a></li></ul>',7)]))}const p=a(i,[["render",o]]);export{h as __pageData,p as default};
